import { useState } from "react";
import "./Carousel.css";
import Card from "./Card";


/** Carousel: displays images and arrows to navigate through them
 * 
 * Props:
 * - photos: array of {src, caption} objects
 * - title: string describing the collection of images
 * 
 * State:
 * - currCardIdx: integer for current card index
 * 
 * App --> Carousel --> Card
 */
 function Carousel({ photos, title }) {
  const [currCardIdx, setCurrCardIdx] = useState(0);
  const [leftIsShowing, setLeftIsShowing] = useState(false);
  const [rightIsShowing, setRightIsShowing] = useState(false);

  const currCard = photos[currCardIdx];
  const total = photos.length;

  function updateArrows() {
    /*set defaults = visible*/
    setLeftIsShowing(true);
    setRightIsShowing(true);

    /*hide arrows based on currCardIdx*/
    if (currCardIdx === 0) {
      setLeftIsShowing(false);
    }
    if (currCardIdx === total-1) {
      setRightIsShowing(false)
    }
  }


  function goForward() {
    setCurrCardIdx(currCardIdx+1);
    updateArrows();
  }



  //Decrements currCardIdx by 1, or if already viewing first image, moves to last image (total-1 index) 

  /*original goBack() function that simply goes from 0 to the 'last' image in an array*/
  function goBack() {
    setCurrCardIdx(currCardIdx-1);
    updateArrows();
  }


  return (
    <div className="Carousel">
      <h1>{title}</h1>
      <div className="Carousel-main">
        <i

          className="bi bi-arrow-left-circle"
          onClick={goBack}
          style={{visibility: leftIsVisible ? "visible":"hidden"}}

        />
        <Card
          caption={currCard.caption}
          src={currCard.src}
          currNum={currCardIdx + 1}
          totalNum={total}
        />
        <i
          className="bi bi-arrow-right-circle"
          onClick={goForward}
          style={{visibility: rightIsVisible ? "visible":"hidden"}}
        />
      </div>
    </div>
  );
}

export default Carousel;
